    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Admin - Incyte{% endblock %}
{% block stylesheets %}
    {{ parent() }}
    {{ encore_entry_link_tags('admin') }}
{% endblock %}
{% block body %}

    {% include '/admin/congres/_navbar.html.twig' %}
    <style>label.required{display: contents}</style>
    <div class=\"container\">
        <h3 class=\"pb-4\"><a class=\"pr-3\" href=\"{{ path('admin_congres_documents', {id: congres.id}) }}\" >&#8249;</a>Editer un document</h3>
        <div class=\"row bg-white p-3 shadow-lg\">
            <div class=\"col-12\">
                {% for message in app.flashes('sucess') %}
                    <div class=\"alert alert-success\">
                        {{ message }}
                    </div>
                {% endfor %}
                {{ form_start(form) }}
                <div class=\"my-custom-class-for-errors\">
                    {{ form_errors(form) }}
                </div>

                <div class=\"row pb-5\">
                    <div class=\"col\">
                        <div class=\"pb-5\">
                            {{ form_row(form.docName) }}
                            <embed src=\"{{ asset('uploads/documents/') }}{{ document.docName }}\" width=100% height=500 type='application/pdf'/>
                        </div>
                        {{ form_row(form.img) }}
                        <img src=\"{{ asset('uploads/img/document/') }}{{ document.img }}\" width=\"100px\">
                    </div>
                    <div class=\"col mb-5\">
                        {{ form_row(form.name) }}
                        {{ form_row(form.about) }}
                        {{ form_row(form.ref) }}
                        {{ form_row(form.categoryDocuments) }}
                    </div>
                </div>
                {{ form_end(form) }}
            </div>
        </div>
    </div>

{% endblock %}

", "admin/document/editDocument.html.twig", "/var/www/vhosts/evenements-incyte.com/httpdocs/templates/admin/document/editDocument.html.twig");
